    public int numSimilarGroups(String[] strs) {
        int[] parent = new int[strs.length];
        int[] rank = new int[strs.length];

        int count = strs.length;
        System.out.println("count = " + count);

        for (int i = 0; i < strs.length; i++) {
            parent[i] = i;
        }

        for (int i = 0; i < strs.length; i++) {
            for (int j = i + 1; j < strs.length; j++) {
                if (find(i, parent) != find(j, parent)) {
                    if (diff(strs[i], strs[j]) <= 2) {
                        union(i, j, parent, rank);
                        count--;
                    }
                }
            }
        }
        return count;
    }

    private int find(int index, int[] parent) {
        if (parent[index] == index) {
            return index;
        }
        return find(parent[index], parent);
    }

    private void union(int i, int j, int[] parent, int[] rank) {
        int index1 = find(i, parent);
        int index2 = find(j, parent);

        if (index1 == index2) {
            return;
        }

        if (rank[index1] < rank[index2]) {
            parent[index1] = index2;
        } else if (rank[index2] < rank[index1]) {
            parent[index2] = index1;
        } else {
            parent[index1] = index2;
            rank[index2]++;
        }
    }
    private int diff(String s1, String s2) {
        int count = 0;
        for (int i = 0; i < s1.length(); i++) {
            if (s1.charAt(i) != s2.charAt(i)) {
                count++;
                if (count > 2) {
                    return count;
                }
            }
        }
        return count;
    }
